mapper.select:
select bno, idx, writer, reg_time,parent_bno,content,avail,groupnum from
(select rownum rn, bno, idx, writer, reg_time,parent_bno,content,avail,groupnum  
from(
select * from reply where 



avail=1 
order by reg_time desc 
)
where 


rownum<=#{pageNum}*#{amount} 
) where
rn>(#{pageNum}-1)*#{amount}

select idx,title,content,writer,reg_time,update_time,rnt,good,email from 
(
select /*+index_desc(board board_pk) */ rownum rn,idx,title,content,writer,reg_time,update_time,rnt,good,email from board 
where
 delete_yn='N' and



rownum<=#{pageNum}*#{amount} 
) where
rn>(#{pageNum}-1)*#{amount}
